      levelChangeData,
      closeLevelChangeModal,
      // Nouvelles fonctions pour la comparaison
      isLevelUpgrade,
      isRoleUpgrade,
      // Fonctions pour les points
      viewPointsHistory,
      closePointsHistoryModal,
      adjustPoints,
      closePointsAdjustModal,
      savePointsAdjustment,
