import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import AppLayout from '@/layout/AppLayoutGlobal.vue';
import { ref, reactive, onMounted, watch, nextTick } from 'vue';
import api from '@/services/api';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import Chart from 'chart.js/auto';
export default {
  name: 'AdminReportsPage',
  components: {
    AppLayout
  },
  setup() {
    const filters = reactive({
      period: '30',
      includeUsers: true,
      includeObjects: true,
      includeFamilies: true,
      includeActivity: true,
      exportFormat: 'pdf'
    });
    const isGenerating = ref(false);
    const reportData = ref(null);
    const objectTypesChart = ref(null);

    // Fonction pour générer le rapport
    const generateReport = async () => {
      try {
        isGenerating.value = true;

        // Récupérer le token d'authentification
        const token = localStorage.getItem('token');
        if (!token) {
          throw new Error('Non authentifié');
        }

        // Configurer l'en-tête d'authentification
        api.defaults.headers.common['Authorization'] = `Bearer ${token}`;

        // Appel API pour récupérer les données pour le rapport
        const params = {
          period: filters.period
        };
        const response = await api.get('/history/admin-report', {
          params
        });

        // Traitement des données (simulé ici)
        // En pratique, on utiliserait les données réelles de l'API
        reportData.value = {
          users: {
            total: response.data.usersCount || 5,
            byLevel: response.data.usersByLevel || {
              'débutant': 2,
              'intermédiaire': 0,
              'avancé': 1,
              'expert': 2
            },
            new: response.data.newUsers || 1,
            active: response.data.activeUsers || 4,
            avgLoginsPerDay: response.data.avgLoginsPerDay || 3.5
          },
          objects: {
            total: response.data.objectsCount || 8,
            byType: response.data.objectsByType || {
              'Four': 2,
              'Réfrigérateur': 1,
              'Robot': 2,
              'Micro-ondes': 1,
              'Mixeur': 2
            },
            totalUsage: response.data.totalObjectsUsage || 124,
            avgUsagePerDay: response.data.avgObjectsUsagePerDay || 12.4,
            mostUsed: response.data.mostUsedObject || 'Four principal'
          },
          families: {
            total: response.data.familiesCount || 3,
            avgSize: response.data.avgFamilySize || 2.7,
            withPoints: response.data.familiesWithPoints || 2,
            totalPoints: response.data.totalPoints || 350,
            avgPointsPerFamily: response.data.avgPointsPerFamily || 116.7,
            recentPoints: response.data.recentPointsDistributed || 50
          },
          activity: {
            total: response.data.actionsCount || 250,
            byType: response.data.actionsByType || {
              'connexion': 45,
              'utilisation_objet': 124,
              'ajout_points': 15,
              'creation_recette': 8,
              'modification_utilisateur': 12,
              'autre': 46
            },
            byTime: response.data.actionsByTime || {
              'Matin (00h-08h)': 25,
              'Journée (08h-16h)': 120,
              'Soir (16h-00h)': 105
            }
          }
        };
      } catch (error) {
        console.error('Erreur lors de la génération du rapport:', error);
        alert('Erreur lors de la génération du rapport. Veuillez réessayer.');
      } finally {
        isGenerating.value = false;
      }
    };

    // Fonction pour exporter le rapport
    const exportReport = () => {
      if (!reportData.value) return;
      try {
        const timestamp = new Date().toISOString().slice(0, 10);
        const fileName = `rapport_admin_${timestamp}`;
        exportToPDF(fileName);
      } catch (error) {
        console.error('Erreur lors de l\'exportation du rapport:', error);
        alert('Erreur lors de l\'exportation du rapport. Veuillez réessayer.');
      }
    };

    // Fonction pour exporter en PDF
    const exportToPDF = fileName => {
      const doc = new jsPDF();

      // Ajouter un en-tête avec logo (simulation)
      doc.setFillColor(44, 80, 56);
      doc.rect(0, 0, 210, 30, 'F');

      // Titre avec logo
      doc.setTextColor(255, 255, 255);
      doc.setFontSize(22);
      doc.setFont('helvetica', 'bold');
      doc.text('Smart Kitchen', 105, 15, {
        align: 'center'
      });
      doc.setFontSize(14);
      doc.setFont('helvetica', 'normal');
      doc.text('Rapport d\'administration', 105, 24, {
        align: 'center'
      });

      // Retour à la couleur de texte par défaut
      doc.setTextColor(0, 0, 0);

      // Date et informations
      doc.setFontSize(10);
      doc.text(`Date: ${new Date().toLocaleDateString('fr-FR')}`, 20, 40);
      doc.text(`Période analysée: ${filters.period === 'all' ? 'Tout l\'historique' : `${filters.period} derniers jours`}`, 20, 46);
      doc.text(`Généré par: Administration Smart Kitchen`, 20, 52);
      let yPos = 65;

      // Utilisateurs
      if (filters.includeUsers) {
        doc.setFontSize(16);
        doc.setFont('helvetica', 'bold');
        doc.setTextColor(44, 80, 56);
        doc.text('Statistiques des utilisateurs', 105, yPos, {
          align: 'center'
        });
        doc.setFont('helvetica', 'normal');
        doc.setTextColor(100, 100, 100);
        doc.setFontSize(10);
        doc.text('Analyse de l\'activité et répartition des utilisateurs par niveau', 105, yPos + 6, {
          align: 'center'
        });
        doc.setTextColor(0, 0, 0);
        yPos += 15;

        // Tableau par niveau
        const userLevelData = Object.entries(reportData.value.users.byLevel).map(([level, count]) => {
          const percentage = (count / reportData.value.users.total * 100).toFixed(1);
          return [getNiceLevelName(level), count.toString(), `${percentage}%`];
        });
        autoTable(doc, {
          startY: yPos,
          head: [['Niveau', 'Nombre', 'Pourcentage']],
          body: userLevelData,
          theme: 'grid',
          headStyles: {
            fillColor: [44, 80, 56],
            textColor: [255, 255, 255],
            fontStyle: 'bold'
          },
          styles: {
            fontSize: 10
          },
          columnStyles: {
            0: {
              cellWidth: 60
            },
            1: {
              cellWidth: 40,
              halign: 'center'
            },
            2: {
              cellWidth: 40,
              halign: 'center'
            }
          }
        });
        yPos = doc.lastAutoTable.finalY + 15;

        // Activité
        const userActivityData = [['Nouveaux utilisateurs', reportData.value.users.new.toString()], ['Moyenne connexions/jour', reportData.value.users.avgLoginsPerDay.toFixed(1)]];
        autoTable(doc, {
          startY: yPos,
          head: [['Métrique', 'Valeur']],
          body: userActivityData,
          theme: 'grid',
          headStyles: {
            fillColor: [44, 80, 56],
            textColor: [255, 255, 255],
            fontStyle: 'bold'
          },
          styles: {
            fontSize: 10
          },
          columnStyles: {
            0: {
              cellWidth: 100
            },
            1: {
              cellWidth: 40,
              halign: 'center'
            }
          }
        });
        yPos = doc.lastAutoTable.finalY + 20;
      }

      // Objets connectés
      if (filters.includeObjects) {
        // Vérifier si on a besoin d'ajouter une nouvelle page
        if (yPos > 200) {
          doc.addPage();

          // Ajouter un petit en-tête sur la nouvelle page
          doc.setFillColor(44, 80, 56);
          doc.rect(0, 0, 210, 10, 'F');
          doc.setTextColor(255, 255, 255);
          doc.setFontSize(8);
          doc.text('Smart Kitchen - Rapport d\'administration', 105, 6, {
            align: 'center'
          });
          doc.setTextColor(0, 0, 0);
          yPos = 20;
        }
        doc.setFontSize(16);
        doc.setFont('helvetica', 'bold');
        doc.setTextColor(44, 80, 56);
        doc.text('Statistiques des objets connectés', 105, yPos, {
          align: 'center'
        });
        doc.setFont('helvetica', 'normal');
        doc.setTextColor(100, 100, 100);
        doc.setFontSize(10);
        doc.text('Distribution et utilisation des appareils Smart Kitchen', 105, yPos + 6, {
          align: 'center'
        });
        doc.setTextColor(0, 0, 0);
        yPos += 15;

        // Tableau des objets par type
        const objectsData = Object.entries(reportData.value.objects.byType).map(([type, count]) => {
          const percentage = (count / reportData.value.objects.total * 100).toFixed(1);
          return [type, count.toString(), `${percentage}%`];
        });
        autoTable(doc, {
          startY: yPos,
          head: [['Type', 'Nombre', 'Pourcentage']],
          body: objectsData,
          theme: 'grid',
          headStyles: {
            fillColor: [44, 80, 56],
            textColor: [255, 255, 255],
            fontStyle: 'bold'
          },
          styles: {
            fontSize: 10
          },
          columnStyles: {
            0: {
              cellWidth: 60
            },
            1: {
              cellWidth: 40,
              halign: 'center'
            },
            2: {
              cellWidth: 40,
              halign: 'center'
            }
          }
        });
        yPos = doc.lastAutoTable.finalY + 20;
      }

      // Familles
      if (filters.includeFamilies) {
        // Vérifier si on a besoin d'ajouter une nouvelle page
        if (yPos > 200) {
          doc.addPage();

          // Ajouter un petit en-tête sur la nouvelle page
          doc.setFillColor(44, 80, 56);
          doc.rect(0, 0, 210, 10, 'F');
          doc.setTextColor(255, 255, 255);
          doc.setFontSize(8);
          doc.text('Smart Kitchen - Rapport d\'administration', 105, 6, {
            align: 'center'
          });
          doc.setTextColor(0, 0, 0);
          yPos = 20;
        }
        doc.setFontSize(16);
        doc.setFont('helvetica', 'bold');
        doc.setTextColor(44, 80, 56);
        doc.text('Statistiques des familles', 105, yPos, {
          align: 'center'
        });
        doc.setFont('helvetica', 'normal');
        doc.setTextColor(100, 100, 100);
        doc.setFontSize(10);
        doc.text('Analyse des groupes familiaux et distribution des points', 105, yPos + 6, {
          align: 'center'
        });
        doc.setTextColor(0, 0, 0);
        yPos += 15;

        // Informations générales
        const familiesGeneralData = [['Nombre total de familles', reportData.value.families.total.toString()], ['Taille moyenne (membres)', reportData.value.families.avgSize.toFixed(2)], ['Familles avec points', reportData.value.families.withPoints.toString()]];
        autoTable(doc, {
          startY: yPos,
          head: [['Métrique', 'Valeur']],
          body: familiesGeneralData,
          theme: 'grid',
          headStyles: {
            fillColor: [44, 80, 56],
            textColor: [255, 255, 255],
            fontStyle: 'bold'
          },
          styles: {
            fontSize: 10
          },
          columnStyles: {
            0: {
              cellWidth: 100
            },
            1: {
              cellWidth: 40,
              halign: 'center'
            }
          }
        });
        yPos = doc.lastAutoTable.finalY + 10;

        // Distribution des points
        const pointsData = [['Total des points', reportData.value.families.totalPoints.toFixed(2)], ['Moyenne par famille', reportData.value.families.avgPointsPerFamily.toFixed(2)], ['Points distribués récemment', reportData.value.families.recentPoints.toFixed(2)]];
        autoTable(doc, {
          startY: yPos,
          head: [['Métrique', 'Valeur']],
          body: pointsData,
          theme: 'grid',
          headStyles: {
            fillColor: [44, 80, 56],
            textColor: [255, 255, 255],
            fontStyle: 'bold'
          },
          styles: {
            fontSize: 10
          },
          columnStyles: {
            0: {
              cellWidth: 100
            },
            1: {
              cellWidth: 40,
              halign: 'center'
            }
          }
        });
        yPos = doc.lastAutoTable.finalY + 20;
      }

      // Activité
      if (filters.includeActivity) {
        // Vérifier si on a besoin d'ajouter une nouvelle page
        if (yPos > 200) {
          doc.addPage();

          // Ajouter un petit en-tête sur la nouvelle page
          doc.setFillColor(44, 80, 56);
          doc.rect(0, 0, 210, 10, 'F');
          doc.setTextColor(255, 255, 255);
          doc.setFontSize(8);
          doc.text('Smart Kitchen - Rapport d\'administration', 105, 6, {
            align: 'center'
          });
          doc.setTextColor(0, 0, 0);
          yPos = 20;
        }
        doc.setFontSize(16);
        doc.setFont('helvetica', 'bold');
        doc.setTextColor(44, 80, 56);
        doc.text('Statistiques d\'activité', 105, yPos, {
          align: 'center'
        });
        doc.setFont('helvetica', 'normal');
        doc.setTextColor(100, 100, 100);
        doc.setFontSize(10);
        doc.text('Analyse des interactions et tendances d\'utilisation', 105, yPos + 6, {
          align: 'center'
        });
        doc.setTextColor(0, 0, 0);
        yPos += 15;

        // Répartition par type d'action
        const activityTypeData = Object.entries(reportData.value.activity.byType).map(([action, count]) => {
          const percentage = (count / reportData.value.activity.total * 100).toFixed(1);
          return [formatActionName(action), count.toString(), `${percentage}%`];
        });
        autoTable(doc, {
          startY: yPos,
          head: [['Type d\'action', 'Nombre', 'Pourcentage']],
          body: activityTypeData,
          theme: 'grid',
          headStyles: {
            fillColor: [44, 80, 56],
            textColor: [255, 255, 255],
            fontStyle: 'bold'
          },
          styles: {
            fontSize: 10
          },
          columnStyles: {
            0: {
              cellWidth: 80
            },
            1: {
              cellWidth: 40,
              halign: 'center'
            },
            2: {
              cellWidth: 40,
              halign: 'center'
            }
          }
        });
        yPos = doc.lastAutoTable.finalY + 10;

        // Répartition temporelle
        const timeData = Object.entries(reportData.value.activity.byTime).map(([period, count]) => {
          const percentage = (count / reportData.value.activity.total * 100).toFixed(1);
          return [period, count.toString(), `${percentage}%`];
        });
        autoTable(doc, {
          startY: yPos,
          head: [['Période', 'Nombre d\'actions', 'Pourcentage']],
          body: timeData,
          theme: 'grid',
          headStyles: {
            fillColor: [44, 80, 56],
            textColor: [255, 255, 255],
            fontStyle: 'bold'
          },
          styles: {
            fontSize: 10
          },
          columnStyles: {
            0: {
              cellWidth: 80
            },
            1: {
              cellWidth: 60,
              halign: 'center'
            },
            2: {
              cellWidth: 40,
              halign: 'center'
            }
          }
        });
        yPos = doc.lastAutoTable.finalY + 15;
      }

      // Ajouter une conclusion et signature
      if (yPos > 240) {
        doc.addPage();
        yPos = 20;
      }
      doc.setFontSize(14);
      doc.setFont('helvetica', 'bold');
      doc.setTextColor(44, 80, 56);
      doc.text('Conclusion', 105, yPos, {
        align: 'center'
      });
      yPos += 10;
      doc.setFont('helvetica', 'normal');
      doc.setTextColor(0, 0, 0);
      doc.setFontSize(10);
      doc.text('Ce rapport a été généré automatiquement par Smart Kitchen.', 20, yPos);
      yPos += 6;
      doc.text('Les données présentées reflètent l\'activité de la plateforme et sont destinées à l\'usage exclusif', 20, yPos);
      yPos += 6;
      doc.text('de l\'équipe d\'administration. Pour toute question concernant ce rapport, veuillez contacter', 20, yPos);
      yPos += 6;
      doc.text('le service technique à support@smartkitchen.com', 20, yPos);

      // Signature
      yPos += 20;
      doc.setFontSize(12);
      doc.setFont('helvetica', 'bold');
      doc.text('Service d\'administration', 20, yPos);
      yPos += 6;
      doc.setFont('helvetica', 'italic');
      doc.setFontSize(10);
      doc.text('Smart Kitchen - Votre cuisine intelligente', 20, yPos);

      // Pied de page sur toutes les pages
      const totalPages = doc.internal.getNumberOfPages();
      for (let i = 1; i <= totalPages; i++) {
        doc.setPage(i);

        // Ligne de séparation
        doc.setDrawColor(200, 200, 200);
        doc.line(20, 285, 190, 285);

        // Pied de page
        doc.setTextColor(150, 150, 150);
        doc.setFontSize(8);
        doc.setFont('helvetica', 'normal');
        doc.text(`Smart Kitchen - Rapport généré le ${new Date().toLocaleDateString('fr-FR')}`, 105, 290, {
          align: 'center'
        });
        doc.text(`Page ${i} / ${totalPages}`, 190, 290, {
          align: 'right'
        });
      }
      doc.save(`${fileName}.pdf`);
    };

    // Fonctions utilitaires
    const getNiceLevelName = level => {
      const names = {
        'débutant': 'Débutant',
        'intermédiaire': 'Intermédiaire',
        'avancé': 'Avancé',
        'expert': 'Expert'
      };
      return names[level] || level;
    };
    const formatActionName = action => {
      const actions = {
        'connexion': 'Connexion',
        'deconnexion': 'Déconnexion',
        'creation_utilisateur': 'Création d\'utilisateur',
        'modification_utilisateur': 'Modification d\'utilisateur',
        'utilisation_objet': 'Utilisation d\'objet',
        'ajout_points': 'Ajout de points',
        'creation_recette': 'Création de recette',
        'autre': 'Autre action'
      };
      return actions[action] || action;
    };

    // Fonction pour créer le graphique des types d'objets
    const createObjectTypesChart = () => {
      if (!reportData.value || !objectTypesChart.value) return;
      const ctx = objectTypesChart.value.getContext('2d');
      const types = Object.keys(reportData.value.objects.byType);
      const counts = Object.values(reportData.value.objects.byType);
      new Chart(ctx, {
        type: 'pie',
        data: {
          labels: types,
          datasets: [{
            data: counts,
            backgroundColor: ['#4CAF50', '#8BC34A', '#CDDC39', '#FFC107', '#FF9800', '#FF5722'],
            borderWidth: 1
          }]
        },
        options: {
          responsive: true,
          plugins: {
            legend: {
              position: 'right'
            },
            title: {
              display: true,
              text: 'Répartition des objets par type'
            }
          }
        }
      });
    };
    onMounted(() => {
      // Si les données sont déjà chargées, créer le graphique
      if (reportData.value) {
        createObjectTypesChart();
      }
    });

    // Observer les changements des données du rapport pour créer le graphique
    watch(reportData, () => {
      // Utiliser nextTick pour s'assurer que le DOM est mis à jour
      nextTick(() => {
        createObjectTypesChart();
      });
    });
    return {
      filters,
      isGenerating,
      reportData,
      generateReport,
      exportReport,
      getNiceLevelName,
      formatActionName,
      objectTypesChart
    };
  }
};
